frappe.ui.form.on("Timesheet","refresh",function(frm) {
	if(cur_frm.doc.signature_json){
		var sign = ""
		sign = JSON.parse("[" + cur_frm.doc.signature_json + "]")
		console.log(sign[0])
                $('[data-fieldname="signature"]').show() 
		$('[data-fieldname="signature"]').signature()
                $('[data-fieldname="signature"]').signature({disabled: true});  
		$('[data-fieldname="signature"]').signature("draw", sign[0])
                $('[data-fieldname="signature_svg"]').css("width", "5cm")
        }
        else {
          $('[data-fieldname="signature"]').hide()
        }  
})

frappe.ui.form.on("Customer", "refresh", function(frm) {
	if(!cur_frm.doc.__islocal) {
		cur_frm.add_custom_button(__('Make Log'), 
		function() {
			console.log("hihihi")
			click_on_log()                      
		}, "icon-exclamation", "btn-default make_log");
	}
})


click_on_log = function(name){
        if (frappe.route_options){
		console.log("in frappe.route_options",frappe.route_options)
                var contact_person = frappe.route_options["contact_person"]
                var phone_number = frappe.route_options["mobile_no"]
                var call_receive_time = frappe.route_options["call_receive_time"]
                frappe.route_options = null;
		tn = frappe.model.make_new_doc_and_get_name('Call Logs');
		locals['Call Logs'][tn].contact_person = contact_person
		locals['Call Logs'][tn].phone_number = phone_number
                locals['Call Logs'][tn].client = cur_frm.doc.name 
		locals['Call Logs'][tn].start_time = call_receive_time
		locals['Call Logs'][tn].call_attendant = user
                frappe.set_route('Form', 'Call Logs', tn);
        }
        else{ 
		frappe.model.open_mapped_doc({
        		method: "switsol.switsol.make_user.get_call_logs_from_customer",
        		frm: cur_frm
    		})
	}     
}

frappe.ui.form.on('Contact', 'refresh', function(frm) {
	if(!cur_frm.doc.__islocal && frappe.route_options){
	if(frappe.route_options["doctype"] && frappe.route_options["doc_name"] && frappe.route_options["new_contact"] == "Yes"){
                frappe.route_options["contact_person"] = cur_frm.doc.name
		frappe.route_options["mobile_no"] = cur_frm.doc.mobile_no

		frappe.set_route('Form', frappe.route_options["doctype"], frappe.route_options["doc_name"]); 
           //frappe.route_options = null;    
	}
        if(frappe.route_options["mobile_no"]){
       		cur_frm.set_value("mobile_no",frappe.route_options["mobile_no"]) 
        }
     }
})

frappe.ui.form.on('Contact', 'validate', function(frm) {
	if(!cur_frm.doc.__islocal){
		frappe.route_options["new_contact"] = "Yes"
	}
})

